$(document).ready(function(){$("#location").keyup(function(){var t=$(this).val();""!=t&&$.ajax({url:"../locationsearch.php",method:"POST",data:{query:t},success:function(t){$("#locationList").fadeIn(),$("#locationList").html(t)}})}),$(document).on("click","li",function(){$("#location").val($(this).text()),$("#locationList").fadeOut()})});